package main

import (
	"fmt"
)

func main() {
	part1()
	part2()
}

func uniq(s string) bool {
	uniq := make(map[rune]int, len(s))
	for _, c := range s {
		uniq[c] += 1
	}
	return len(s) == len(uniq)
}

func part1() {
	i := 3
	for {
		if !uniq(input1[i-3 : i+1]) {
			i += 1
			continue
		}

		fmt.Println(input1[i-3:i+1], i+1)
		break
	}
}

func part2() {
	i := 13
	for {
		if !uniq(input1[i-13 : i+1]) {
			i += 1
			continue
		}

		fmt.Println(input1[i-13:i+1], i+1)
		break
	}
}

var (
	input1 = ""
)
